import React, { Component } from 'react'
import Card from "./card/Card"
import Header from './header/Header'

export default class home extends Component {
    constructor(props) {
        super(props)
        this.state = {
            games: this.props.games,
            tempGames: this.props.games
        }

    }
    changeText=(text)=>{
      this.setState({tempGames: this.state.games})
      if(text==""||text==null){
        this.render()
      }else{
        var temp2Games=[]
        this.state.games.map(game=>{
          if(game.name.includes(text)){
              temp2Games.push(game)
          } 
      })
      this.setState({tempGames: temp2Games})
      this.render()
      }
    }
    render() {
        return (
            <div>
                <Header changeText={this.changeText}/>
                <div className="container">
                    {this.state.games.map(game => (
                        <Card game={game} />
                    ))}
                </div>
            </div>
        )
    }
}
