import React, { Component } from 'react'
import Card from "./card/Card"
import Header from './header/Header'

export default class home extends Component {
    constructor(props) {
        super(props)
        this.state = {
            games: this.props.games,
            tempGames: this.props.games,
            searchValue: ""
        }

    }
    changeText=(text)=>{
        this.setState({searchValue: text})
      this.setState({tempGames: this.state.games})
      if(text==""||text==null){
        this.setState({tempGames: this.state.games})
        this.render()
      }else{
        this.setState({tempGames: this.state.games.filter(game => game.name.startsWith(text))})
        this.render()
      }
    }
    render() {
        return (
            <div>
                <Header changeText={this.changeText}/>
                <div className="container">
                    {this.state.tempGames.map(game => (
                        <Card game={game} />
                    ))}
                </div>
            </div>
        )
    }
}
