    switch (value) {
        case 0:
            return empty
        case 100:
            return full
        default:
            return empty
    }
}



const Rating = (props) => {
    const [rating, setRating] = useState(null);
    var score=props.props.getascore
    var user=props.props.user
    function setRate(ratingValue){
        score=((props.props.score*props.user)+ratingValue)/props.props.user+1
        user=props.props.user+1
