import React, { Component } from 'react'
import empty from "../../../styles/empty.png"
import half from "../../../styles/half.png"
import full from "../../../styles/full.png"
import "./rating.scss"

function getStar(value){
    switch(value){
        case 0:
            return empty
        case 50:
            return half
        case 100:
            return full
        default:
            return empty
    }
}
function getStars(value){
    switch (parseFloat(value)){
        case 0.0:
            return [0,0,0,0,0]
        case 0.5:
            return [50,0,0,0,0]
        case 1.0:
            return [100,0,0,0,0]
        case 1.5:
            return [100,50,0,0,0]
        case 2.0:
            return [100,100,0,0,0]
        case 2.5:
            return [100,100,50,0,0]
        case 3.0:
            return [100,100,100,0,0]
        case 3.5:
            return [100,100,100,50,0]
        case 4.0:
            return [100,100,100,100,0]
        case 4.5:
            return [100,100,100,100,50]
        case 5.0:
            return [100,100,100,100,100]
    }
}
export default class Rating extends Component {
    render() {
        return (
            <div>
                {
                    getStars(2.5).map(value => (
                        <img src={getStar(value)} alt/>
                    ))
                }
            </div>
        )
    }
}
