import React, { Component } from 'react'
import empty from "../../../styles/empty.png"
import half from "../../../styles/half.png"
import full from "../../../styles/full.png"
import "./rating.scss"

function getStar(value){
    switch(value){
        case 0:
            return empty
        case 100:
            return full
        default:
            return empty
    }
}
function getStars(value){
    switch (parseFloat(value)){
        case 0.0:
            return [0,0,0,0,0]
        case 1.0:
            return [100,0,0,0,0]
        case 2.0:
            return [100,100,0,0,0]
        case 3.0:
            return [100,100,100,0,0]
        case 4.0:
            return [100,100,100,100,0]
        case 5:
            return [100,100,100,100,100]
        default:
            return[0,0,0,0]

    }
}
export default class Rating extends Component {
    render() {
        return (
            <div className="rating">
                {
                    [...Array(5)].map((i)=>{
                        const ratingValue=i+1;
                        return(
                            <label>
                                <input
                                    type="radio"
                                    name="rating"
                                    value={ratingValue}
                                    onClick={getStars(ratingValue).map(value =>(
                                        <img className="star-img"src={getStar(value)} alt=""/>
                                    ))}
                                />
                            </label>

                        )
                    }
                        )


                }
            </div>
        )
    }
}
