import React, { Component } from 'react'
import Card from "./card/Card"
import Header from './header/Header'

export default class home extends Component {
    constructor(props) {
        super(props)
        this.state = {
            games: props.games,
            tempGames:props.games
        }

    }
    changeText(text){
        this.state.tempGames=this.state.games
      if(text==""){
        this.render()
      }else{
        this.state.tempGames=[]
        this.state.games.map(game=>{
          if(game.name.includes(text)){
              this.state.tempGames.push(game)
          } 
      })
      this.render()
      }
    }
    render() {
        return (
            <div>
                <Header changeText={this.changeText}/>
                <div className="container">
                    {this.state.tempGames.map(game => (
                        <Card game={game} />
                    ))}
                </div>
            </div>
        )
    }
}
